"use strict";
var app = getApp();
Page({
    data: {
        motto: 'Hello World',
        userInfo: {},
        hasUserInfo: false,
        canIUse: wx.canIUse('button.open-type.getUserInfo'),
        canIUseGetUserProfile: false,
        canIUseOpenData: wx.canIUse('open-data.type.userAvatarUrl') && wx.canIUse('open-data.type.userNickName'),
        inputValue: '',
        inputMaxLen: 10,
    },
    bindViewTap: function () {
        wx.navigateTo({
            url: '../logs/logs',
        });
    },
    onLoad: function () {
        if (wx.getUserProfile) {
            this.setData({
                canIUseGetUserProfile: true
            });
        }
    },
    getUserProfile: function () {
        var _this = this;
        wx.getUserProfile({
            desc: '展示用户信息',
            success: function (res) {
                console.log(res);
                _this.setData({
                    userInfo: res.userInfo,
                    hasUserInfo: true
                });
            }
        });
    },
    getUserInfo: function (e) {
        console.log(e);
        this.setData({
            userInfo: e.detail.userInfo,
            hasUserInfo: true
        });
    },
    bindKeyInput: function (e) {
        var cvt = this.convertMoney(e.detail.value);
        this.setData({
            inputValue: cvt.money,
            inputMaxLen: cvt.maxLen,
        });
    },
    convertMoney: function (value) {
        var money = value;
        var maxLen = 10;
        var dotPos = money.indexOf('.');
        if (dotPos >= 0) {
            money = money.substring(0, dotPos + 3);
            if (dotPos + 3 <= value.length) {
                maxLen = dotPos + 3 + 1;
                if (maxLen > 10) {
                    maxLen = 10;
                }
            }
        }
        return { money: money, maxLen: maxLen };
    },
});
